function onload()
  createOff()
  black={0,0,0}
  white={1,1,1}
end

function createButtons()
  createOff()
  createKickBtn()
  createMuteBtn()
  createPromoteBtn()
  createBlindBtn()
end

function turnOff()
    btns=self.getButtons()
    if(btns~=nil)then
        for i,btn in pairs(btns)do
            self.removeButton(i-1)
        end
    end
    createOn()
    self.setColorTint(black)
end

function turnOn()
    btns=self.getButtons()
    if(btns~=nil)then
        for i,btn in pairs(btns)do
            self.removeButton(i-1)
        end
    end
    createOff()
    createButtons()
    self.setColorTint(white)
end


function createOff()
  self.createButton({
    label="Off", click_function="turnOff", function_owner=self,
    position={7.1, 0.2,-4.1}, rotation={0,-0,0}, height=400, width=400, font_size=200
  })
end

