ObjectStates.LuaScript:
CONFIG = {
    MIN_VALUE = -99,
    MAX_VALUE = 999,
    TOOLTIP_SHOW = true,
    VALUE = 0,
    FONT_SIZE = 600
}
FONT_COLOR = "#000000"
ALPHA = 95
rebound=false
NAME = "Counter"

function onLoad(save)
    local assetRoot = 'https://raw.githubusercontent.com/RobMayer/TTSLibrary/master/ui/';
    local assets = {
        {name='ui_plus', url=assetRoot..'plus.png'},
        {name='ui_power', url=assetRoot..'power.png'},
        {name='ui_minus', url=assetRoot..'minus.png'},
        {name='ui_drop', url=assetRoot..'drop.png'},
        {name='ui_checkon', url=assetRoot..'checkbox_on.png'},
        {name='ui_checkoff', url=assetRoot..'checkbox_off.png'},
    }
    self.UI.setCustomAssets(assets)
    Wait.frames(rebuildUI, 5)
    ui_mode = '0'
end

function extract(obj)
    LC = obj.getTable('CONFIG')
    CONFIG.MIN_VALUE = LC.MIN_VALUE or CONFIG.MIN_VALUE
    CONFIG.MAX_VALUE = LC.MAX_VALUE or CONFIG.MAX_VALUE
    COLOR = LC.FONT_COLOR or {0, 0, 0, 95}
    ALPHA = COLOR[4]
    FONT_COLOR = LC.COLOR_HEX or FONT_COLOR
    CONFIG.TOOLTIP_SHOW =  LC.TOOLTIP_SHOW or CONFIG.TOOLTIP_SHOW
    NAME = obj.getName()
    CONFIG.FONTS_SIZE = LC.FONT_SIZE or CONFIG.FONT_SIZE
    rebuildUI()
end

function inject(obj)
    function f()
        obj.script_state = ""
        obj.setName(NAME)
        CONFIG.FONT_COLOR = colorAsRGBA()
        CONFIG.COLOR_HEX = FONT_COLOR
        obj.setTable('CONFIG', CONFIG)
        obj.call('updateSave')
        obj.reload()
    end
    local req = WebRequest.get("https://raw.githubusercontent.com/Eldinnie/ttsluastuff/master/counterv2.ttslua", function(res)
        if (not(res.is_error)) then
            Wait.condition(f, function() return obj.setLuaScript(res.text) end)
        else
            error(res)
        end
    end)
end

function ui_setmode(player, value, id)
    ui_mode = value
    rebuildUI()
end

function setMinVal(player, value, id)
    CONFIG.MIN_VALUE = tonumber(value)
    rebuildUI()
end

function setName(player, value, id)
    NAME = value
    rebuildUI()
end

function setMaxVal(player, value, id)
    CONFIG.MAX_VALUE = tonumber(value)
    rebuildUI()
end

function setFontSize(player, value, id)
    CONFIG.FONT_SIZE = tonumber(value)
    rebuildUI()
end


function toggleToolip(player, value, id)
    CONFIG.TOOLTIP_SHOW = not CONFIG.TOOLTIP_SHOW
    rebuildUI()
end

function setColor(player, value, id)
    FONT_COLOR = value
    rebuildUI()
end

function setOpacity(player, value, id)
    ALPHA = tonumber(value)
    if not rebound then
        rebuildUI()
        rebound=true
        Wait.time(function() rebound=false end, 0.1)
    end
end

function colorAsRGBA()
    return_color = {tonumber(FONT_COLOR:sub(2,3), 16)/255, tonumber(FONT_COLOR:sub(4,5), 16)/255, tonumber(FONT_COLOR:sub(6,7), 16)/255, ALPHA}
    return return_color
end

function colorAsString()
    color = colorAsRGBA()
    color[4] = math.floor(color[4])/100
    return "rgba("..color[1]..","..color[2]..","..color[3]..","..color[4]..")"
end

function ui_inject(player, value, id)
    local pos = self.getPosition()
    local size = self.getBoundsNormalized().size
    for i,hit in pairs( Physics.cast({ origin = {x=pos.x, y=pos.y + 0, z=pos.z}, direction = {x=0, y=1, z=0}, max_distance = 1})) do
        if (hit.hit_object ~= self and hit.hit_object.tag ~= "Surface") then
            if (hit.hit_object.getLuaScript() ~= "") then
                broadcastToAll("Skip GUID: ".. hit.hit_object.getGUID() .." already has a script.\nremove script before trying again", "Red")
            else
                inject(hit.hit_object)
            end
        end
    end
end



function ui_clearmini(player)
    local pos = self.getPosition()
    for i,hit in pairs( Physics.cast({ origin = {x=pos.x, y=pos.y + 0, z=pos.z}, direction = {x=0, y=1, z=0}, max_distance = 1 })) do
        if (hit.hit_object ~= self) then
            if hit.hit_object.getVar('INJECTED_COUNTER') then
                hit.hit_object.setLuaScript("")
                hit.hit_object.script_state = ""
                hit.hit_object.clearInputs()
                hit.hit_object.clearButtons()
                hit.hit_object.reload()
            end
        end
    end
end

function takeFrom(player)
    local pos = self.getPosition()
    for i,hit in pairs(Physics.cast({ origin = {x=pos.x, y=pos.y + 0, z=pos.z}, direction = {x=0, y=1, z=0}, max_distance = 1 })) do
        if (hit.hit_object ~= self) then
            if hit.hit_object.getVar('INJECTED_COUNTER') then
                extract(hit.hit_object)
            end
        end
    end
end

