function onSave()
    --We make a table of data we want to save. WE CAN ONLY SAVE 1 TABLE.
    local data_to_save = {name=name,ath=ath,com=com,eng=eng,pil=pil,sci=sci,mov=mov,hea=hea,tar=tar,luc=luc,han=han,rank=rank,pr=pr,xp=xp,cr=cr,notes=notes}
    --We use this command to convert the table into a string
    saved_data = JSON.encode(data_to_save)
    --And this inserts the string into the save information for this script.
    return saved_data
    --Data is now saved.
end

--Runs when the map is first loaded
function onload(saved_data)
    --First we check if there was information saved in this script yet
    if saved_data ~= "" then
        --If there is save data, first we convert the string back to a table
        local loaded_data = JSON.decode(saved_data)
        --Then we pull out data out of the table
        ath = loaded_data.ath
        com = loaded_data.com
        eng = loaded_data.eng
        pil = loaded_data.pil
        sci = loaded_data.sci
        mov = loaded_data.mov
        hea = loaded_data.hea
        tar = loaded_data.tar
        luc = loaded_data.luc
        han = loaded_data.han
        rank = loaded_data.rank
        pr = loaded_data.pr
        xp = loaded_data.xp
        cr = loaded_data.cr
        name = loaded_data.name
        notes = loaded_data.notes
    end

--function onLoad()
  CreateButtons()
end


