skullChar = string.char(0x2620)
notChar = string.char(0x2298)
handChar = string.char(0x270C)
wornChar = string.char(0x2606)
  massnum = 0
  energy = "e"
  danger = skullChar
  hands = handChar
  editing = true
  name = "Name"
  description = "Description"
  quick = "Quick\n Look"

function updateSave()
  onSave()
end

function onSave()
    local data_to_save = {
      danger=danger,
      hands=hands,
      editing=editing,
      name=name,
      description=description,
      quick=quick,
      massnum=massnum,
      energy=energy
    }
    saved_data = JSON.encode(data_to_save)
    return saved_data
end

function onload(saved_data)
  print("loading")
  if saved_data ~= nil then
    loaded_data = JSON.decode(saved_data)
    massnum = loaded_data.massnum
    energy = loaded_data.energy
    danger = loaded_data.danger
    hands = loaded_data.hands
    editing = loaded_data.editing
    name = loaded_data.name
    description = loaded_data.description
    quick = loaded_data.quick
  end
  createEditInputs()
  createEditButtons()
  done()
end


