function editName(_obj, _string, value)
  name = value
  updateSave()
end
function editDesc(_obj, _string, value)
  description = value
  updateSave()
end
function editQuick(_obj, _string, value)
  quick = value
  updateSave()
end


function alterDanger(_obj, _color, alt_click)
  if danger == '' then
    danger = skullChar
    self.editButton({index = 0, label = danger, font_size = 160})
  elseif danger == skullChar then
    danger = notChar
    self.editButton({index = 0, label = danger, font_size = 240})
  elseif danger == notChar then
    danger = skullChar .. notChar
    self.editButton({index = 0, label = danger, font_size = 120})
  else
    danger = ''
    self.editButton({index = 0, label = danger})
  end
  updateSave()
end


function alterMass(_obj, _color, alt_click)
    mod = alt_click and -1 or 1
    new_value = math.min(math.max(massnum + mod, -10), 17)
    if massnum ~= new_value then
        massnum = new_value
        if massnum == 16 then
          self.editButton({index = 1, label = '75m'})
        elseif massnum == 17 then
          self.editButton({index = 1, label = '100m'})
        else
          self.editButton({index = 1, label = massnum .. 'm'})
        end
        updateSave()
    end
end


function alterEnergy(_obj, _color, alt_click)
  if energy == '' then
    energy = 'e'
    self.editButton({index = 2, label = energy, font_color = {0.8,0.8,0.2,100}})
  elseif energy == 'e' then
    energy = 'EMP'
    self.editButton({index = 2, label = energy, font_size = 120, font_color = {0.2,0.8,0.8,100}})
  else
    energy = ''
    self.editButton({index = 2, label = energy})
  end
  updateSave()
end


function alterHand(_obj, _color, alt_click)
  if hands == '' then
    hands = handChar
    self.editButton({index = 3, label = hands, font_size = 160, font_color = {0.8,0.8,0.2,100}})
  elseif hands == handChar then
    hands = wornChar
    self.editButton({index = 3, label = hands, font_size = 160, font_color = {0.8,0.8,0.2,100}})
  elseif hands == wornChar then
    hands = "pack"
    self.editButton({index = 3, label = hands, font_size = 80, font_color = {0.6,0.4,0.2,100}})
  else
    hands = ''
    self.editButton({index = 3, label = hands, font_color = {0.6,0.4,0.2,100}})
  end
  updateSave()
end


function done()
  self.editButton({index = 0, width = 0, height = 0})
  self.editButton({index = 1, width = 0, height = 0})
  self.editButton({index = 2, color = {0,0,0,0}})
  self.editButton({index = 3, color = {0,0,0,0}})
  self.editButton({index = 4, width = 0, height = 0, label = ""})
  self.editButton({index = 5, click_function = "reEdit", label = "edit", color = {0,0,0,0}, font_color = {0,0,0,100}})
  editing = false
  updateSave()
end

function reEdit()
  self.editButton({index = 0, width = 225, height = 225})
  self.editButton({index = 1, width = 225, height = 225})
  self.editButton({index = 2, color = {1,1,1,1}})
  self.editButton({index = 3, color = {1,1,1,1}})
  self.editButton({index = 4, width = 1000, height = 220, label = "Image"})
  self.editButton({index = 5, click_function = "done", label = "Done", color = {1,1,1,1}, font_color = {0,0,0,1}})
  editing = true
end

function imageChange()
  createImageButtons()
end

